int main(int argc, const char* argv[])
{
#if _WINDOWS
	char drive[MAX_PATH];
	char dir[MAX_PATH];
	char filename[MAX_PATH];
	char ext[MAX_PATH];
	_splitpath(argv[0], drive, dir, filename, ext);

	std::string root;
	root += drive;
	root += dir;

	::SetCurrentDirectory(root.c_str());
#endif

	try
	{
		if (argc <= 2)
			throw std::runtime_error("too few arguments to function 'main'.\n");

		int w = 1920, h = 1080;

		auto app = octoon::GameApp::instance();
		app->setGameListener(nullptr);
		app->open(nullptr, w, h, w, h);
		app->start();

		auto object = std::make_shared<octoon::GameObject>();
		object->addComponent<PathMeshingComponent>(argv[2]);

		app->update();
		app->close();
	}
	catch (const std::exception& e)
	{
		std::cout << e.what();
	}
}